## LinkedList完善_增加泛型

```java
package cn.LinkedList;

/**
 * 自定义一个链表
 * @author HQF
 *
 */
public class LinkedList01<E> {
	private Node first;
	private Node last;
	private int size;

	//添加节点
	public void add(int index,E element) {//alt+shirt +r 替换
		Node newNode = new Node(element);
		Node temp = getNode(index);

		if (temp!=null) {
			Node up = temp.previous;

			up.next = newNode;
			newNode.previous = up;

			newNode.next = temp;
			temp.previous = newNode;
		}
	}

	//移除指定的元素
	public void remove(int index) {

		checkRange(index);

		Node temp = getNode(index);

		if (temp!=null) {
			Node up = temp.previous;
			Node down = temp.next;

			if (up!=null) {
				up.next = down;
			}

			if (down!=null) {
				down.previous = up;
			}

			if (index == 0) {
				first = down;
			}

			if (index == size-1) {
				last = up;
			}
			size--;
		}
	}

	//取出C元素，索引数为2
	public E get(int index) {
		System.out.println(size);

		Node temp = getNode(index);

		return temp!=null?(E)temp.element:null;
	}

	//处理索引的index合不合法
	private void checkRange(int index) {
		//异常处理
		if ((index<0)||(index>size-1)) {
			throw new RuntimeException("索引数字不合法"+index);
		}
	}

	private Node getNode( int index) {
		checkRange(index);

		Node temp = null;

		//如果index大于size一半则从后便利，否则从前面遍历
		if (index<=(size>>1)) {//size>>1相当于除以2
			temp = first;
			for (int i = 0; i < index; i++) {
				temp = temp.next;
			}		
		}else {
			temp = last;
			for (int i = size-1; i > index; i--) {
				temp = temp.previous;
			}
		}
		return temp;
	}

	//[]
	//['a','b','c']
	public void add(E element) {
		Node node = new Node(element);

		if (first==null) {
			first = node;
			last = node;
		}else {

			node.previous = last;
			node.next = null;

			last.next = node;
			last = node;
		}
		size++;
	}
	@Override
	public String toString() {
		//[a,b,c]	first = a,	last = c;
		StringBuilder sBuilder = new StringBuilder("[");
		Node temp = first;
		while (temp!=null) {
			sBuilder.append(temp.element+",");
			temp = temp.next;
		}
		//setCharAt要使用单引号，否则会报错
		sBuilder.setCharAt(sBuilder.length()-1, ']');
		return sBuilder.toString();
	}

	public static void main(String[] args) {
		LinkedList01<String> linkedList = new LinkedList01<>();

		linkedList.add("a");
		linkedList.add("b");
		linkedList.add("c");
		linkedList.add("d");
		linkedList.add("e");
		linkedList.add("f");

		System.out.println(linkedList);
		System.out.println(linkedList.get(4));
		linkedList.add(3,"JF");
		System.out.println(linkedList);
		System.out.println(linkedList.get(1));
	}
}

