			function checkUsername(username){
				var usernameMsg = document.getElementById("usernameMsg");

				var length = username.value.length;
				if (length < 3 || length > 6) {
					usernameMsg.innerText = "用户名长度必须是3 - 6位";
				} else{
					usernameMsg.innerText = "";
				}
			}

