## HashMap

```java
package cn.Map;
/**
 * HashMap
 * put
 * toStringMap
 * @author HQF
 * @param <V>
 *
 */
public class TestHashMap<k,V> {
	Node2[] table;//bucket array
	int size;		//
	public TestHashMap() {
		table = new Node2[16];
	}

	public V get(k key) {
		int hash = myHash(key.hashCode(), table.length);
		V value = null;
		if (table[hash]!=null) {
			Node2 temp = table[hash];
			while (temp!=null) {
				if (temp.key.equals(key)) {
					//value
					value = (V)temp.value;
					break;
				}else {
					temp = temp.next;
				}
			}
		}
		return value;
	}

	public void put(k key,V value) {
		//
		//
		Node2 newNode = new Node2();
		newNode.hash = myHash(key.hashCode(), table.length);
		newNode.key = key;
		newNode.value = value;
		newNode.next = null;

		Node2 temp = table[newNode.hash];
		//
		Node2 iterLast = null;
		boolean keyRepeat = false;

		if (temp==null) {
			//
			table[newNode.hash] = newNode;
			size++;
		}else {
			//
			while (temp!=null) {
				//key
				if (temp.key.equals(key)) {
					keyRepeat = true;
				//valuehashkeynext
					temp.value = value;
					break;
				}else {
				//key
				iterLast = temp;
				temp = temp.next;
			}
				if (!keyRepeat) {
					//key
					iterLast.next = newNode;
					size++;
				}
		iterLast.next = newNode;
			}
	}
	}

	@Override
	public String toString() {
		// TODO Auto-generated method stub
		StringBuilder sBuilder = new StringBuilder("{")  ;

		for (int i = 0; i < table.length; i++) {
			Node2 temp = table[i];
			while(temp!=null) {
				sBuilder.append(temp.key+":"+temp.value+",");
				temp = temp.next;
			}
		}
		sBuilder.setCharAt(sBuilder.length()-1, '}');
		return sBuilder.toString();
	}

	public static int myHash(int v,int length) {
//		//
//		System.out.println("hash in myHash:"+(v&(length-1)));
//		//
//		System.out.println("hash in myHash:"+(v%(length-1)));
		return v&(length-1);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		TestHashMap<Integer,String> m = new	TestHashMap();
		m.put(10, "aa");
		m.put(20, "bb");

		System.out.println(m);	
//		for (int i = 10; i < 100; i++) {
//			System.out.println(i + "---"+myHash(i,16));//53,69,85 hash
//		}
	System.out.println(m.get(53));
	}
}

```

