		this.money = money;
		this.name = name;
	}

}

//
class Drawing extends Thread{
	Acount acount;//
	int drawingMoney;//
	int drawingTotal;//
	int packetTotal;//


	public Drawing(Acount acount, int drawingMoney,String name) {
		super(name);
		this.acount = acount;
		this.drawingMoney = drawingMoney;
	}

	@Override
	public void run() {
		if (acount.money - drawingMoney < 0) {
			return;
		}

		try {
			Thread.sleep(1000);
		} catch (InterruptedException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}

		acount.money -= drawingMoney;
		packetTotal += drawingMoney;
		System.out.println(this.getName() + "-->" + acount.money);
		System.out.println(this.getName() + "-->" +packetTotal);

	}
}
