				System.out.println(s);
			}

			System.out.println("===================================");
			// listFiles()
			File[] subFiles = dir.listFiles();
			for (File file : subFiles) {
				System.out.println(file);
			}

			System.out.println("===================================");
			//
			File[] roots = dir.listRoots();
			for (File file : roots) {
				System.out.println(file);
			}
	}

